import { createSlice, configureStore} from "@reduxjs/toolkit";


const initialAuthState = {
  isAuthenticated: false,
};

const authSlice = createSlice({
  name: 'authentication',
  initialState: initialAuthState,
  reducer: {
    login(state){
      state.isAuthenticated = true;
    },
    logout(state){
      state.isAuthenticated = false;
    }
  }
})


const initialCounterState = { counter : 0, showCounter: true};

const counterSlice = createSlice(
  {
    name: 'counter',
    initialState: initialCounterState,
    reducers: {

      increment(state){
        state.counter++;
      },
      decrement(state){
        state.counter--;
      },
      increase(state, action){
        state.counter = state.counter + action.payload
      },
      toggoleCounter(state){
        state.showCounter = !state.showCounter
      }
    }
  }
);




// 리듀서의 값이 단일 리듀서가 될 수 있다.
// slice가 여러개이더라도 리덕스 스토어는 하나만 존재할 수 있다.
// 개별적인 여러개의 reducer를 입력하면 나중에 하나의 주요 리듀서로 자동으로 합쳐진다.
// 그리고 주요 리듀서를 스토어에 노출한다.

const store = configureStore({
  reducer: { auth: authSlice.reducer, counter: counterSlice.reducer}
});

//전역으로 액션 객체를 생성한다. 원하는 메서드만 뽑아 와 사용할 수 있게 함.
export const {counterActions} = counterSlice.actions;
export const {authActions} = authSlice.actions;
export default store;
